import { useState } from 'react';
import Banner from './Components/Banner';
import Fomulario from './Components/Formulario';
import Time from './Components/Times';

function App() {
  const times = [
    {
      nome:"Programação",
      corPrimeria:"#57c278",
      corSecunaria:"",
    },
    {
      nome:"Front-End",
      corPrimeria:"",
      corSecunaria:"",
    },
    {
      nome:"Data Science",
      corPrimeria:"",
      corSecunaria:"",
    },
    {
      nome:"Devops",
      corPrimeria:"",
      corSecunaria:"",
    },
    {
      nome:"UX e Design",
      corPrimeria:"",
      corSecunaria:"",
    },
    {
      nome:"Mobile",
      corPrimeria:"",
      corSecunaria:"",
    },
    {
      nome:"Inovação e Gestão",
      corPrimeria:"",
      corSecunaria:"",
    }
  ];
  const [colaboradores,setColaboradores] = useState([])

  const aoNovoColabroradorAdicionado = (colaborador)=>{
    console.log(colaborador)
    setColaboradores([...colaboradores,colaborador])
  }

  return (
    <div className="App">
      <Banner/>
      <Fomulario aoColaboradorCadastrado={colaborador=>aoNovoColabroradorAdicionado(colaborador)}/>
      <Time nome="Programação"/>
      <Time nome="Front-End"/>
    </div>
  );
}
export default App;
