import { useState } from 'react';
import Banner from './Components/Banner';
import Fomulario from './Components/Formulario';
import Time from './Components/Times';

function App() {
  const [colaboradores,setColaboradores] = useState([])

  const aoNovoColabroradorAdicionado = (colaborador)=>{
    console.log(colaborador)
    setColaboradores([...colaboradores,colaborador])
  }

  return (
    <div className="App">
      <Banner/>
      <Fomulario aoColaboradorCadastrado={colaborador=>aoNovoColabroradorAdicionado(colaborador)}/>
      <Time nome="Programação"/>
      <Time nome="Front-End"/>
    </div>
  );
}
export default App;
