import { useState } from 'react';
import Banner from './Components/Banner';
import Fomulario from './Components/Formulario';
import Time from './Components/Times';

function App() {
  const times = [
    {
      nome:"Programação",
      corPrimeria:'#57c278',
      corSecunaria:'#d9f7e9',
    },
    {
      nome:"Front-End",
      corPrimeria:"#82cffa",
      corSecunaria:"#e8f8ff",
    },
    {
      nome:"Data Science",
      corPrimeria:"#a6d157",
      corSecunaria:"#f0f8e2",
    },
    {
      nome:"Devops",
      corPrimeria:"#e06b69",
      corSecunaria:"#fde7e8",
    },
    {
      nome:"UX e Design",
      corPrimeria:"#db6ebf",
      corSecunaria:"#fae9f5",
    },
    {
      nome:"Mobile",
      corPrimeria:"#ffba05",
      corSecunaria:"#fff5d9",
    },
    {
      nome:"Inovação e Gestão",
      corPrimeria:"#ff8a29",
      corSecunaria:"#ffeedf",
    }
  ];
  const [colaboradores,setColaboradores] = useState([])

  const aoNovoColabroradorAdicionado = (colaborador)=>{
    console.log(colaborador)
    setColaboradores([...colaboradores,colaborador])
  }

  return (
    <div className="App">
      <Banner/>
      <Fomulario Times = {times.map(time=>time.nome)} aoColaboradorCadastrado={colaborador=>aoNovoColabroradorAdicionado(colaborador)}/>
      {times.map(times=><Time key={times.nome} nome ={times.nome} corPrimaria ={times.corPrimeria} corSecundaria ={times.corSecunaria}/>)}
    </div>
  );
}
export default App;
